import React from "react";

import Sort from "../Components/Sort";
import Categories from "../Components/Categories";
import FoodBlock from "../Components/FoodItems/FoodBlock";
import LoadingFoodBlog from "../Components/FoodItems/LoadingFoodBlock";

const Home = () => {
  const [items, setItems] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [category, setCategory] = React.useState(0);
  const [sort, setSort] = React.useState({
    name: "popular",
    sortProperty: "rating",
  });

  React.useEffect(() => {
    setIsLoading(true);
    // const category = ;
    const sortBy = sort.sortProperty.replace("-", " ");
    const order = sort.sortProperty.includes("-") ? "asc" : "desc";

    fetch(
      `https://6293b734089f87a57ac4de66.mockapi.io/items?${
        category > 0 ? `category=${category}` : ""
      }&sortBy=${sortBy}&order=${order}`
    )
      .then((res) => {
        return res.json();
      })
      .then((json) => {
        setItems(json);
        setIsLoading(false);
      });
  }, [category, sort]);

  console.log(category, sort);
  return (
    <div className="container">
      <div className="content__top">
        <Categories
          category={category}
          onClickCategory={(index) => setCategory(index)}
        />
        <Sort sort={sort} onClickSort={(index) => setSort(index)} />
      </div>
      <h2 className="content__title">All food</h2>
      <div className="content__items">
        {isLoading
          ? [...new Array(10)].map((_, index) => (
              <LoadingFoodBlog key={index} />
            ))
          : items.map((obj) => (
              <FoodBlock
                key={obj.id}
                title={obj.title}
                price={obj.price}
                imageUrl={obj.imageUrl}
                sizes={obj.sizes}
                types={obj.types}
              />
            ))}
      </div>
    </div>
  );
};
export default Home;
