import "./scss/app.scss";

import React from "react";
import Categories from "./Components/Categories";
import Header from "./Components/Header";
import Sort from "./Components/Sort";
import FoodBlock from "./Components/FoodItems/FoodBlock";
import LoadingFoodBlog from "./Components/FoodItems/LoadingFoodBlock";

// import items from "./assets/items.json";

function App() {
  const [items, setItems] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);

  React.useEffect(() => {
    fetch("https://6293b734089f87a57ac4de66.mockapi.io/items")
      .then((res) => {
        return res.json();
      })
      .then((json) => {
        setItems(json);
      });
    setIsLoading(false);
  }, []);

  return (
    <>
      <div className="wrapper">
        <Header />
        <div className="content">
          <div className="container">
            <div className="content__top">
              <Categories />
              <Sort />
            </div>
            <h2 className="content__title">All food</h2>
            <div className="content__items">
              {isLoading
                ? [...new Array(10)].map((_, index) => (
                    <LoadingFoodBlog key={index} />
                  ))
                : items.map((obj) => (
                    <FoodBlock
                      key={obj.id}
                      title={obj.title}
                      price={obj.price}
                      imageUrl={obj.imageUrl}
                      sizes={obj.sizes}
                      types={obj.types}
                    />
                  ))}

              {/* <% include components/pizza-block.ejs %> <% include components/pizza-block.ejs %> <%
            include components/pizza-block.ejs %> <% include components/pizza-block.ejs %> <%
            include components/pizza-block.ejs %> <% include components/pizza-block.ejs %> <%
            include components/pizza-block.ejs %> <% include components/pizza-block.ejs %> <%
            include components/pizza-block.ejs %> */}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
