import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [],
};
const itemSlice = createSlice({
  name: "item",
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload.items;
    },
  },
});

export const { setItems } = itemSlice.actions;

export default itemSlice.reducer;
