import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [],
};
const foodSlice = createSlice({
  name: "food",
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload.items;
    },
  },
});

export const { setItems } = foodSlice.actions;

export default foodSlice.reducer;
